INSERT INTO EShopdb.tb_product (product_id, product_type_id, product_name, old_product_price, new_product_price, product_spec, product_size, product_flag, product_broad, product_stock, product_pic, product_remain_time) VALUES (14, 2, '小羊倌孕妇羊奶粉', null, 168, '<p><span style="font-size: 12pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本品采用天然牧养的莎能奶山羊的生羊乳为奶源，依照孕产妇的特殊营养需求，特别添加了低聚果糖、DHA（脑黄金）、叶酸、碳酸钙、维生素等营养元素。羊奶中有丰富的上皮细胞生长因子（EGF），可以修复表皮细胞，阻碍黑色素形成，对肌肤的滋润平滑起到很好的修复作用，对色斑的形成有预防作用。因此，怀孕后每天坚持喝一杯羊奶的孕妇妊娠斑比不喝羊奶的发生率低，色斑也比较少且颜色浅。其中配方羊奶粉含有大量的烟酸、牛磺酸、烟酸参与催乳素的生成，牛磺酸刺激人体乳腺组织，产后多喝羊奶对产妇有良好的催奶作用。</span></p>', '600克/罐 12罐/件 7200克/件', 0, null, 123, 'img/product/1387931161425.jpg', '2019-01-11 17:00:00');
INSERT INTO EShopdb.tb_product (product_id, product_type_id, product_name, old_product_price, new_product_price, product_spec, product_size, product_flag, product_broad, product_stock, product_pic, product_remain_time) VALUES (15, 5, '小羊倌无蔗糖奶片', null, 30, '<p><span style="background: white; color: black; font-size: 12pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 无糖奶片以山羊奶粉、乳清粉、禾甜糖为原料压制而成，其蛋白质含量高、营养丰富、脂肪球小、易消化吸收，且添加的禾甜糖是益生保健糖，在提供优美甜味的同时，还为人们提供</span><span style="background: white; color: black; font-size: 12pt">&ldquo;</span><span style="background: white; color: black; font-size: 12pt">益生元</span><span style="background: white; color: black; font-size: 12pt">&rdquo;</span><span style="background: white; color: black; font-size: 12pt">，改善肠道健康，提高免疫力。食用不导致龋齿、不导致发胖、不增高血糖、深受糖尿病患者以及追求健康饮食人群的喜爱。</span></p>', '140克/盒 20盒/件 2800克/件', 1, null, 123, 'img/product/1387931243469.jpg', '2019-01-11 17:00:00');
INSERT INTO EShopdb.tb_product (product_id, product_type_id, product_name, old_product_price, new_product_price, product_spec, product_size, product_flag, product_broad, product_stock, product_pic, product_remain_time) VALUES (16, 5, '小羊倌有糖奶片', null, 25, '<p><span style="background: white; color: #333333; font-size: 12pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本品是以鲜山羊奶为主要原料，配以适量白砂糖，低聚糖，乳清粉等混合压缩而制成的一种新型片壮乳制品，其蛋白质含量高，脂肪球小，易消化吸收。</span><span style="font-size: 12pt">适合各年龄段人群饮用。</span></p>', '140克/盒 20盒/件 2800克/件', 1, null, 123, 'img/product/1387931243469.jpg', '2019-01-11 17:00:00');
